INSERT INTO `jobSys` (`job_id`, `job_title`, `company_name`, `industry`, `salary_range`, `degree`, `experience`, `location`, `description`, `skill`, `welfare`, `status`, `created_at`, `updated_at`) VALUES (804, '高级客户经理（武汉）', '安超云软件有限公司', '云计算', '10-15K', '大专', '5-10年', '武汉', '工作职责：1、主要负责湖北区域以武汉为主，云计算相关产品的市场开拓和维护，完成销售业绩指标；2、学习私有云、超融合、虚拟化、云存储、云桌面、云原生、云操作系统、信创等产品知识和销售技巧，赋能客户以及合作伙伴；3、通过直客、渠道、生态或联合体的方式在行业内构建多层次多元化的客户关系；4、资源拉通，经营运作头部客户，提升客户满意度以及产品影响力。任职要求：1、全面了解精通所在行业及目标客户；2、足够的行业直客及渠道资源积累；3、充分掌握行业业务特点及应对策略；4、专业的行业业务及项目运作技巧及经验；5、熟悉教育、政府、制造业、医疗等行业中1-2个行业优先考虑源；', '私有云销售', '节日福利 绩效奖金 定期体检 五险一金 企业年金 保底工资 底薪加提成 年终奖', '1', '2025-04-10 15:11:22', '2025-04-10 15:11:22');
