INSERT INTO `jobSys` (`job_id`, `job_title`, `company_name`, `industry`, `salary_range`, `degree`, `experience`, `location`, `description`, `skill`, `welfare`, `status`, `created_at`, `updated_at`) VALUES (825, '分布式存储平台后端开发', '九维', '云计算', '200-350元/天', '本科', '', '武汉', '产品开发与维护：负责存储产品的开发和维护工作，确保产品质量和性能 。技术攻坚与创新：对后端开发工程化问题进行探索，提出建设性的解决方案，持续改善已有服务，优化系统薄弱点，重点、难点的技术攻坚，及对新技术的探索 。团队协作：与产品经理、设计师、前端开发工程师、测试人员等团队成员协作，共同优化产品各项参数和整体效果 。存储平台系统设计：参与项目的需求分析、架构设计、系统设计，负责核心业务后端功能的架构设计与优化、应用开发与测试 。技术选型与方案设计：参与行业技术方向的讨论，设计行业技术选型，以及项目各类功能块方案的设计与功能服务的落地 。性能调优：负责代码性能分析与性能调优工作，参与持续集成和自动化部署相关设计和开发 。岗位要求：扎实的Go语言编程能力，熟悉数据结构和数据库优化。熟悉Linux系统及命令操作，具备Docker、k8s环境开发部署经验。了解分布式存储及其相关概念，例如：Ceph，Lustre等良好的沟通能力和团队协作能力，能够独立思考分析需求功能。', 'Golang Gin Kubernetes 计算机相关专业 Golang服务端开发经验', '五险一金 年终奖 定期体检 生日福利 餐补 零食下午茶 保底工资 股票期权 有无线网 绩效奖金 员工旅游 交通补助 法定节假日三薪 带薪年假 加班补助 节假日加班费', '1', '2025-04-10 15:11:22', '2025-04-10 15:11:22');
INSERT INTO `jobSys` (`job_id`, `job_title`, `company_name`, `industry`, `salary_range`, `degree`, `experience`, `location`, `description`, `skill`, `welfare`, `status`, `created_at`, `updated_at`) VALUES (826, 'GPU算力集群开发', 'PPIO', '云计算', '16-30K·15薪', '本科', '5-10年', '武汉', '职位概述：我们正在寻找一位技术过硬、热爱Linux、热爱容器技术的软件工程师加入我们的团队。作为我们团队的核心成员，您将负责设计、开发和维护分布式的 GPU 算力集群系统，为我们的产品提供稳定可靠的技术支持。职责描述：1、设计、开发和维护高性能、可扩展的 GPU 算力集群方案，确保其稳定性和安全性；2、与前端工程师、平台开发工程师、产品经理和其他团队成员合作，根据业务场景的需求，设计稳定、可靠以扩展的系统架构，并形成完善的设计文档；3、编写清晰、高效的代码，并进行代码审查和不断的迭代优化，确保代码质量和性能4、实施自动化测试和持续集成/持续部署流程，确保产品质量和交付效率5、跟踪并采用最新的系统开发技术和工具，不断优化系统架构和性能岗位要求：1、熟悉 Linux 操作系统，精通 Golang 或 C/C++，熟练使用 Shell/Python，有大型项目经验者优先，对分布式系统设计和实现有经验者优先；2、至少 2 年以上相关的开发经验，有本科学位者和有计算机相关专业背景者优先；3、熟悉容器化技术，如 docker，runc, containerd，能够非常熟练的使用 docker 命令；了解分布式存储系统如 ceph 者优先；4、深入理解 Linux 的 namespace, cgroups 等概念，掌握 veth pair, bridge, macvlan, ipvlan, tun/tap 等网络设备和 tcp/udp/kcp/tls 等网络协议。5、具备良好的问题分析和解决能力，能够快速定位并解决技术问题；6、良好的团队合作精神和沟通能力，能够有效与团队成员协作完成项目任务；7、对技术发展趋势和新技术（如 AI 技术）持续关注，并有持续学习的动力和能力。8、熟悉各介质安装部署技能，具有独立运维交付环境的能力。', 'Golang Docker GPU Kubernetes', '团建聚餐 夜班补助 定期体检 法定节假日三薪 年终奖 员工旅游 带薪年假 补充医疗保险 保底工资 节假日加班费 绩效奖金 工龄奖 零食下午茶 节日福利 股票期权 意外险 交通补助 有无线网 餐补 五险一金', '1', '2025-04-10 15:11:22', '2025-04-10 15:11:22');
